import { useState } from "react";
import { MemoryCards } from "./components/Card/MemoryCards";
import imageGame1 from "./assets/planet-game1.png";
import imageGame2 from "./assets/planet-game2.png";
import imageGame3 from "./assets/planet-game3.png";
import imageGame4 from "./assets/planet-game4.png";
import imageGame5 from "./assets/planet-game5.png";
import imageGame6 from "./assets/planet-game6.png";

import './App.css';
import { useEffect } from "react";


const cardImages = [
  {"src": imageGame1},
  {"src": imageGame2},
  {"src": imageGame3},
  {"src": imageGame4},
  {"src": imageGame5},
  {"src": imageGame6}
]

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);

  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
    .sort(() => Math.random() - 0.5)
    .map((card) => ({ ...card, id: Math.random() }))

    setCards(shuffledCards);
    setTurns(0);
  }

  const handleChoice = (card) => {
    console.log('aca')
    choiceOne === null ? setChoiceOne(card) : setChoiceTwo(card);
  }

  const resetTurn = () => {
    setChoiceOne(null);
    setChoiceTwo(null);
    setTurns((prevTurns) => prevTurns + 1);
  };

  useEffect(() => {
    console.log(choiceOne, 'choice');
  }, [choiceOne]);

  // const handleComparation = () => {
  //   if (choiceOne && choiceTwo) {
  //     if (choiceOne === choiceTwo) {
  //       console.log('itMatch');
  //     } else {
  //       console.log('not')
  //     }
  //   }
  // }

  useEffect(() => {
    if (choiceOne === null && choiceTwo) {
      if (choiceOne.src === choiceTwo.src) {
        console.log('itMatch');
        resetTurn();
      } else {
        console.log('not');
        resetTurn();
      }
    }
  }, [choiceOne, choiceTwo])
  return (
    <div className="App">
      <h1>Magic Match</h1>
      <button onClick={shuffleCards}>New Game</button>
      <MemoryCards
        cards={cards}
        handleChoice={handleChoice}
      />

    </div>
  );
}

export default App;
