import { useState } from 'react';
import './App.css';
// import cardImages from './constants/cards-constants';
import bird from "./assets/bird.png"

const cardImages = [
  {"src": '../assets/planet-game1.png'},
  {"src": '../assets/planet-game2.png'},
  {"src": '../assets/planet-game3.png'},
  {"src": '../assets/planet-game4.png'},
  {"src": '../assets/planet-game5.png'},
  {"src": '../assets/planet-game6.png'}
]

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0)
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
    .sort(() => Math.random() - 0.5)
    .map((card) => ({ ...card, id: Math.random() }))

    setCards(shuffledCards);
    setTurns(0);
  }



  console.log(cards, turns);
  return (
    <div className="App">
      <h1>Magic Match</h1>
      <button onClick={shuffleCards}>New Game</button>
      <div className='card-grid'>
        {
          cards.map((card) => (
            <div className='card' key={card.id}>
              <div>
                <img className='front' src={card.src} alt='card front' />
                <img className='back' src='./assets/bird.png' alt='card back'/>
              </div>
            </div>
          ))
        }
      </div>
    </div>
  );
}

export default App;
